using System.Collections; using System.Collections.Generic; using UnityEngine;  public class ScreenCapture : MonoBehaviour {  	Texture2D screenCap; 	Texture2D border; 	bool shot = false;  	// Use this for initialization 	void Start () { 		screenCap = new Texture2D (502, 252, TextureFormat.RGB24, false); 		border = new Texture2D (2, 2, TextureFormat.ARGB32, false); 		border.Apply (); 	}  	// Update is called once per frame 	void Update () { 		if (Input.GetKeyUp (KeyCode.Space)) { 			StartCoroutine ("Capture"); 		} 	}  	void OnGUI() { 		GUI.DrawTexture (new Rect (150, 100, 500, 2), border, ScaleMode.StretchToFill); //top 		GUI.DrawTexture (new Rect (150, 350, 500, 2), border, ScaleMode.StretchToFill); //bottom 		GUI.DrawTexture (new Rect (650, 100, 2, 250), border, ScaleMode.StretchToFill); //right 		GUI.DrawTexture (new Rect (150, 100, 2, 250), border, ScaleMode.StretchToFill); //left  		if (shot) { 			GUI.DrawTexture (new Rect (10, 10, 150, 75), screenCap, ScaleMode.ScaleToFit); 		} 	}  	IEnumerator Capture() { 		yield return new WaitForEndOfFrame (); 		screenCap.ReadPixels(new Rect(150, 100, 502, 252),0, 0); 		screenCap.Apply (); 		shot = true; 	} }  